// reservationService.js
import axios from "axios";

const API_URL = "http://localhost:8080/api";

// Configuration globale d'axios
axios.interceptors.request.use((request) => {
  console.log("Requête sortante:", request);
  return request;
});

axios.interceptors.response.use(
  (response) => {
    console.log("Réponse reçue:", response);
    return response;
  },
  (error) => {
    console.error("Erreur axios:", error.response || error);
    return Promise.reject(error);
  }
);

export const reservationService = {
  getReservations: async () => {
    try {
      console.log("Appel GET reservations...");
      const response = await axios.get(`${API_URL}/reservations`);
      console.log("Données reçues:", response.data);
      return response.data;
    } catch (error) {
      console.error("Erreur getReservations:", {
        message: error.message,
        response: error.response?.data,
        status: error.response?.status,
      });
      throw error;
    }
  },

  createReservation: async (reservationData) => {
    try {
      const response = await axios.post(
        `${API_URL}/reservations`,
        reservationData
      );
      return response.data;
    } catch (error) {
      console.error(
        "Erreur createReservation:",
        error.response?.data || error.message
      );
      throw error;
    }
  },

  updateReservation: async (id, reservationData) => {
    try {
      const response = await axios.put(
        `${API_URL}/reservations/${id}`,
        reservationData
      );
      return response.data;
    } catch (error) {
      console.error(
        "Erreur updateReservation:",
        error.response?.data || error.message
      );
      throw error;
    }
  },

  deleteReservation: async (id) => {
    try {
      await axios.delete(`${API_URL}/reservations/${id}`);
      return true;
    } catch (error) {
      console.error(
        "Erreur deleteReservation:",
        error.response?.data || error.message
      );
      throw error;
    }
  },

  getReservationById: async (id) => {
    try {
      const response = await axios.get(`${API_URL}/reservations/${id}`);
      return response.data;
    } catch (error) {
      console.error(
        "Erreur getReservationById:",
        error.response?.data || error.message
      );
      throw error;
    }
  },
};
