import axios from "axios";

const API_URL = "http://localhost:8080/api";

const transformReservationData = (data) => {
  try {
    console.log("Données brutes reçues du backend:", data);
    return data.map((item) => ({
      id: item.id,
      clientName: item.client
        ? `${item.client.prenom || ""} ${item.client.nom || ""}`.trim() ||
          "Non spécifié"
        : "Non spécifié",
      email: item.client?.email || "Non spécifié",
      phone: item.client?.telephone || "Non spécifié",
      roomType: item.chambre?.type || "Non spécifié",
      startDate: item.dateDebut ? item.dateDebut.toString() : "Non spécifié",
      endDate: item.dateFin ? item.dateFin.toString() : "Non spécifié",
      preferences: item.preferences || "Aucune préférence",
    }));
  } catch (error) {
    console.error("Erreur détaillée transformation:", error);
    console.error("Données problématiques:", data);
    return [];
  }
};

export const reservationService = {
  getReservations: async () => {
    try {
      const response = await axios.get(`${API_URL}/reservations`);
      console.log("Réponse GET reservations:", response.data);
      const transformed = transformReservationData(response.data);
      console.log("Données transformées:", transformed);
      return transformed;
    } catch (error) {
      console.error("Erreur getReservations:", error);
      throw error;
    }
  },

  createReservation: async (formData) => {
    try {
      const nameParts = (formData.clientName || "").split(" ");
      const firstName = nameParts[0] || "";
      const lastName = nameParts.slice(1).join(" ") || "";

      const backendData = {
        client: {
          nom: lastName,
          prenom: firstName,
          email: formData.email || "",
          telephone: formData.phone || "",
        },
        chambre: {
          type: formData.roomType.toUpperCase(),
          prix: 0.0,
          disponible: true,
        },
        dateDebut: formData.startDate,
        dateFin: formData.endDate,
        preferences: formData.preferences || "",
      };

      console.log("Données envoyées au backend:", backendData);
      const response = await axios.post(`${API_URL}/reservations`, backendData);
      console.log("Réponse création:", response.data);
      return response.data;
    } catch (error) {
      console.error("Erreur détaillée:", error.response?.data);
      throw error;
    }
  },

  updateReservation: async (id, formData) => {
    try {
      const nameParts = (formData.clientName || "").split(" ");
      const firstName = nameParts[0] || "";
      const lastName = nameParts.slice(1).join(" ") || "";

      const backendData = {
        client: {
          nom: lastName,
          prenom: firstName,
          email: formData.email || "",
          telephone: formData.phone || "",
        },
        chambre: {
          type: formData.roomType.toUpperCase(),
        },
        dateDebut: formData.startDate,
        dateFin: formData.endDate,
        preferences: formData.preferences || "",
      };

      console.log("Données update envoyées:", backendData);
      const response = await axios.put(
        `${API_URL}/reservations/${id}`,
        backendData
      );
      console.log("Réponse update:", response.data);
      return response.data;
    } catch (error) {
      console.error("Erreur update:", error);
      throw error;
    }
  },

  getReservationById: async (id) => {
    try {
      const response = await axios.get(`${API_URL}/reservations/${id}`);
      console.log("Réservation brute reçue:", response.data);
      const transformed = transformReservationData([response.data])[0];
      console.log("Réservation transformée:", transformed);
      return transformed;
    } catch (error) {
      console.error("Erreur getReservationById:", error);
      throw error;
    }
  },

  deleteReservation: async (id) => {
    try {
      await axios.delete(`${API_URL}/reservations/${id}`);
      return true;
    } catch (error) {
      console.error("Erreur deleteReservation:", error);
      throw error;
    }
  },
};
