// services/reservationService.js
import axios from "axios";

const API_URL = "http://localhost:8080/services/ws";

const createSoapEnvelope = (method, params) => `
  <soapenv:Envelope xmlns:soapenv="http://schemas.xmlsoap.org/soap/envelope/" 
                    xmlns:ser="http://controller.hotelgestion.example.com/">
    <soapenv:Header/>
    <soapenv:Body>
      <ser:${method}>${params}</ser:${method}>
    </soapenv:Body>
  </soapenv:Envelope>
`;

export const reservationService = {
  async getReservations() {
    try {
      const response = await axios.post(
        API_URL,
        createSoapEnvelope("getAllReservations", ""),
        {
          headers: {
            "Content-Type": "text/xml",
          },
        }
      );
      console.log("Response XML:", response.data);
      const parser = new DOMParser();
      const xmlDoc = parser.parseFromString(response.data, "text/xml");
      return this.transformReservations(xmlDoc);
    } catch (error) {
      console.error("Erreur lors de la récupération des réservations:", error);
      throw error;
    }
  },

  async getReservationById(id) {
    try {
      const params = `<arg0>${id}</arg0>`;
      const response = await axios.post(
        API_URL,
        createSoapEnvelope("getReservationById", params),
        {
          headers: {
            "Content-Type": "text/xml",
          },
        }
      );
      const parser = new DOMParser();
      const xmlDoc = parser.parseFromString(response.data, "text/xml");
      return this.transformReservation(xmlDoc.querySelector("return"));
    } catch (error) {
      console.error("Erreur lors de la récupération de la réservation:", error);
      throw error;
    }
  },

  async createReservation(formData) {
    try {
      const [firstName, ...lastNameParts] = formData.clientName.split(" ");
      const params = `
        <arg0>${formData.startDate}</arg0>
        <arg1>${formData.endDate}</arg1>
        <arg2>
          <nom>${lastNameParts.join(" ")}</nom>
          <prenom>${firstName}</prenom>
          <email>${formData.email}</email>
          <telephone>${formData.phone}</telephone>
        </arg2>
        <arg3>
          <type>${formData.roomType}</type>
          <disponible>true</disponible>
          <prix>0.0</prix>
        </arg3>
      `;

      console.log(
        "Request XML:",
        createSoapEnvelope("createReservation", params)
      );

      const response = await axios.post(
        API_URL,
        createSoapEnvelope("createReservation", params),
        {
          headers: {
            "Content-Type": "text/xml",
          },
        }
      );

      console.log("Response XML:", response.data);
      const parser = new DOMParser();
      const xmlDoc = parser.parseFromString(response.data, "text/xml");
      return this.transformReservation(xmlDoc.querySelector("return"));
    } catch (error) {
      console.error("Erreur lors de la création de la réservation:", error);
      throw error;
    }
  },

  async updateReservation(id, formData) {
    try {
      const [firstName, ...lastNameParts] = formData.clientName.split(" ");
      const params = `
        <arg0>${id}</arg0>
        <arg1>${formData.startDate}</arg1>
        <arg2>${formData.endDate}</arg2>
        <arg3>
          <nom>${lastNameParts.join(" ")}</nom>
          <prenom>${firstName}</prenom>
          <email>${formData.email}</email>
          <telephone>${formData.phone}</telephone>
        </arg3>
        <arg4>
          <type>${formData.roomType}</type>
          <disponible>true</disponible>
          <prix>0.0</prix>
        </arg4>
      `;

      const response = await axios.post(
        API_URL,
        createSoapEnvelope("updateReservation", params),
        {
          headers: {
            "Content-Type": "text/xml",
          },
        }
      );
      const parser = new DOMParser();
      const xmlDoc = parser.parseFromString(response.data, "text/xml");
      return xmlDoc.querySelector("return").textContent === "true";
    } catch (error) {
      console.error("Erreur lors de la mise à jour de la réservation:", error);
      throw error;
    }
  },

  async deleteReservation(id) {
    try {
      const params = `<arg0>${id}</arg0>`;
      const response = await axios.post(
        API_URL,
        createSoapEnvelope("deleteReservation", params),
        {
          headers: {
            "Content-Type": "text/xml",
          },
        }
      );
      const parser = new DOMParser();
      const xmlDoc = parser.parseFromString(response.data, "text/xml");
      return xmlDoc.querySelector("return").textContent === "true";
    } catch (error) {
      console.error("Erreur lors de la suppression de la réservation:", error);
      throw error;
    }
  },

  transformReservation(node) {
    if (!node) return null;

    const getNodeValue = (path) => {
      const element = node.querySelector(path);
      return element ? element.textContent : "";
    };

    return {
      id: getNodeValue("id"),
      clientName: `${getNodeValue("client/prenom")} ${getNodeValue(
        "client/nom"
      )}`.trim(),
      email: getNodeValue("client/email"),
      phone: getNodeValue("client/telephone"),
      roomType: getNodeValue("chambre/type"),
      startDate: getNodeValue("dateDebut"),
      endDate: getNodeValue("dateFin"),
      preferences: getNodeValue("preferences") || "",
    };
  },

  transformReservations(xmlDoc) {
    const reservations = xmlDoc.querySelectorAll("return");
    return Array.from(reservations)
      .map((res) => this.transformReservation(res))
      .filter((res) => res !== null);
  },
};
