import axios from "axios";

const API_URL = "http://localhost:8080/api";

const transformReservationData = (data) => {
  // Vérifie si data est un tableau ou un objet avec une propriété item
  const items = Array.isArray(data) ? data : data.List.item;

  return items.map((item) => ({
    id: item.id,
    clientName: item.client ? `${item.client.prenom} ${item.client.nom}` : "",
    email: item.client ? item.client.email : "",
    phone: item.client ? item.client.telephone : "",
    roomType: item.chambre ? item.chambre.type.toLowerCase() : "",
    startDate: item.dateDebut
      ? `${item.dateDebut[0]}-${item.dateDebut[1]}-${item.dateDebut[2]}`
      : "",
    endDate: item.dateFin
      ? `${item.dateFin[0]}-${item.dateFin[1]}-${item.dateFin[2]}`
      : "",
    preferences: item.preferences || "",
  }));
};

export const reservationService = {
  getReservations: async () => {
    try {
      console.log("Appel GET reservations...");
      const response = await axios.get(`${API_URL}/reservations`);
      console.log("Données brutes reçues:", response.data);

      const transformedData = transformReservationData(response.data);
      console.log("Données transformées:", transformedData);

      return transformedData;
    } catch (error) {
      console.error("Erreur getReservations:", {
        message: error.message,
        response: error.response?.data,
        status: error.response?.status,
      });
      throw error;
    }
  },

  createReservation: async (reservationData) => {
    try {
      const response = await axios.post(
        `${API_URL}/reservations`,
        reservationData
      );
      return response.data;
    } catch (error) {
      console.error(
        "Erreur createReservation:",
        error.response?.data || error.message
      );
      throw error;
    }
  },

  updateReservation: async (id, reservationData) => {
    try {
      const response = await axios.put(
        `${API_URL}/reservations/${id}`,
        reservationData
      );
      return response.data;
    } catch (error) {
      console.error(
        "Erreur updateReservation:",
        error.response?.data || error.message
      );
      throw error;
    }
  },

  deleteReservation: async (id) => {
    try {
      await axios.delete(`${API_URL}/reservations/${id}`);
      return true;
    } catch (error) {
      console.error(
        "Erreur deleteReservation:",
        error.response?.data || error.message
      );
      throw error;
    }
  },

  getReservationById: async (id) => {
    try {
      const response = await axios.get(`${API_URL}/reservations/${id}`);
      const transformedData = transformReservationData([response.data])[0];
      return transformedData;
    } catch (error) {
      console.error(
        "Erreur getReservationById:",
        error.response?.data || error.message
      );
      throw error;
    }
  },
};
